<script>
   // Mortgage Calculation
    function mortgage(){
        var total_amount = Number({{$listing->price}});

        var messages = document.querySelectorAll(".mortgage-calculator-fields-minus");
        messages.forEach(message => {
            var attribute_type = $(message).attr('attribute_type');
            var condition = $(message).attr('condition');
            var field_value = Number($(message).val());

            if(attribute_type == 'flat' && field_value > 0){
                total_amount = parseFloat((total_amount-field_value).toFixed(5));
            }else if(field_value > 0){
                total_amount = parseFloat((total_amount - ((total_amount / 100) * field_value)).toFixed(5));
            }
        });

        var messages = document.querySelectorAll(".mortgage-calculator-fields-plus");
        messages.forEach(message => {
            var attribute_type = $(message).attr('attribute_type');
            var condition = $(message).attr('condition');
            var field_value = Number($(message).val());

            if(attribute_type == 'flat' && field_value > 0){
                total_amount = parseFloat((total_amount+field_value).toFixed(5));
            }else if (field_value > 0){
                total_amount = parseFloat((total_amount + ((total_amount / 100) * field_value)).toFixed(5));
            }
        });


        //Monthly
        var loan_terms = parseFloat($('#loan').val());
        total_amount = total_amount / loan_terms; // Divide by loan terms
        // Display the total amount for a single month
        total_amount = total_amount/12;
        new_amount = (total_amount*12)*loan_terms;
        $('#monthly_listings_price').text(total_amount.toFixed(2));
        $('#monthly_price').text(total_amount.toFixed(2));
        $('#new_price').text(new_amount.toFixed(2));

    }
    var mortgageInputs = document.querySelectorAll(".mortgage-body .input");
        mortgageInputs.forEach(function(input) {
            input.addEventListener(".input", mortgage);
        });
        mortgage();
        // Input Value Change
        function changeValue(value, elementId) {
            var changeLabel = document.getElementById(elementId);
            changeLabel.textContent = value;
        }

        // Mortgage Print
        function printableDiv(printableAreaDivId) {
            var printContents = document.getElementById(printableAreaDivId).innerHTML;
            var originalContents = document.body.innerHTML;
        
            document.body.innerHTML = printContents;
        
            window.print();
        
            document.body.innerHTML = originalContents;
         }
  

</script>
