<script>
     "use strict";
$(document).ready(function() {
    // Attach click event handler to the share button
    $('#shareButton').on('click', function() {
        // Get the current page's URL
        var currentPageUrl = window.location.href;

        // Create a temporary input element to copy the URL to clipboard
        var tempInput = document.createElement('input');
        tempInput.value = currentPageUrl;
        document.body.appendChild(tempInput);
        tempInput.select();
        document.execCommand('copy');
        document.body.removeChild(tempInput);

        // Show Toastr notification for successful copy
        toastr.success('Successfully copy this link!');
    });
});
</script>

